// 封装模态框_提示框
// 提示框

// 图标 base64 码  全局变量
// 默认图标
var info_modal = 'data:image/png;base64,';
// 成功图标
var success_modal = 'data:image/png;base64,';
// 警告图标
var warning_modal = 'data:image/png;base64,';
// 错误图标
var error_modal = 'data:image/png;base64,';
// 关闭图标
var close_modal = 'data:image/png;base64,';
// 问号按钮
var question_modal = 'data:image/png;base64,';

function $modal(data) { // data 必传 且为对象
    // 赋值默认参数
    if (data === undefined) {
        data = {}
    }

    // 弹框类型不传默认为 alert
    if (data.type === undefined) {
        data.type = 'alert'
    }

    tipIconImg = ''; //提示显示小图标
    // 提示类型 默认不传为  info
    if (data.icon === 'success') {
        tipIconImg = success_modal
    } else if (data.icon === 'warning') {
        tipIconImg = warning_modal
    } else if (data.icon === 'error') {
        tipIconImg = error_modal
    } else if (data.icon === 'question') {
        tipIconImg = question_modal
    } else {
        tipIconImg = info_modal
    }

    // 弹框显示时长   confirm 下无效 
    if (data.timeout === undefined && data.type != 'confirm') {
        data.timeout = 2000;
    } else {
        // 最短显示时间为500
        data.timeout < 500 ? data.timeout = 500 : data.timeout = data.timeout;
    }

    // 过渡动画
    if (data.transition === undefined) {
        data.transition = 200
    }

    // 遮罩层参数
    if (data.mask === undefined) {
        data.mask
    }

    // 最小宽度
    if (data.width === undefined) {
        data.width = 300
    }

    // 禁止页面滚动
    if (data.pageScroll === undefined) {
        data.pageScroll = true
    }

    // 提示框标题
    if (data.title === undefined) {
        data.title = '提示'
    }

    // 遮罩层关闭
    if (data.maskClose === undefined) {
        data.maskClose = false
    }

    // 取消按钮文字
    if (data.cancelText === undefined) {
        data.cancelText = '取消'
    }

    // 确认按钮文字
    if (data.confirmText === undefined) {
        data.confirmText = "确认"
    }

    // 距离顶部距离
    if (data.top === undefined) {
        data.top = 100
    }

    // 是否绝对剧中
    if (data.center === undefined) {
        data.center = false
    }

    // 公共计算当前元剧中top 值
    function calculate(e, sun) {
        if (data.center) {
            var tipHeight = e.height() + sun;
            console.log(tipHeight)
            var windowHeight = $(window).height();
            console.log((windowHeight / 2) - (tipHeight / 2))
            data.top = (windowHeight / 2) - (tipHeight / 2)
        }
    }

    //  生成相对唯一id  保证弹框绑定id唯一  保证事件绑定不重复  （此处用于保证弹框内层 继续 弹框  不出现事件绑定重复）
    var idText = "modailItem";
    var date = (new Date().getTime() * Math.random() + '').substr(0, 10)
    idText += date;

    var titleSize = 16; //标题文字大小
    var fontSize = 14; //字体大小
    // 创建最外层元素 
    if ($('#modail-dialog-box').index() < 0) {
        var modelBox = '<div id="modail-dialog-box"></div>'
        $('body').append(modelBox)
        // 设置样式
        $('#modail-dialog-box').css({
            fontFamily: '微软雅黑',
            fontSize: fontSize + 'px',
            color: '#666666',
        })
    }

    // 生成 message 提示
    if (data.type === 'message') {
        $('#modail_message_box').html('');
        $('#modail-dialog-box').append('<div id="modail_message_box"></div>')
        // 设置message提示框长度
        var width_s = 40 + (fontSize * data.content.length) + fontSize + 4;
        // 不需要遮罩层
        $('#modail_message_box').append('<div id="' + idText + '_box"></div>');
        // 添加左侧图标
        $('#' + idText + '_box').append('<div class="' + idText + '_item item1"><img src="' + tipIconImg + '" /></div>');
        // 提示内容
        $('#' + idText + '_box').append('<div class="' + idText + '_item item2">' + data.content + '</div>');
        if (data.closable) {
            // 右侧关闭图标
            width_s += fontSize + 4;
            $('#' + idText + '_box').append('<div class="' + idText + '_item item3"><img src="' + close_modal + '" /></div>');
        }
        // 设置外层框样式
        $('#' + idText + '_box').css({
            width: width_s + 'px',
            padding: '10px 15px',
            background: '#fff',
            boxShadow: '0 1px 6px rgba(0,0,0,.2)',
            borderRadius: '5px',
            position: 'fixed',
            left: '0',
            margin: 'auto',
            right: '0',
            opacity: '0',
        })
        // 设置文字内容框样式
        $('.' + idText + '_item.item2').css({
            margin: '0 8px'
        })
        // 设置文字关闭按钮靠边样式
        $('.' + idText + '_item.item3').css({
            position: 'absolute',
            right: '15px',
            cursor: 'pointer'
        })
        // 设置内层框公共样式
        $('.' + idText + '_item').css({
            display: 'inline-block'
        })
        $('.' + idText + '_item img').css({
            width: fontSize + 4 + 'px',
            verticalAlign: 'top',
            position: 'relative',
            top: '1px'
        })
        // 计算 距离顶部距离
        calculate($('#' + idText + '_box'), 20)
        $('#' + idText + '_box').css({
            top: data.top - 60,
        })
        // 显示动画
        $('#' + idText + '_box').animate({
            'top': data.top,
            'opacity': '1',
        }, data.transition)

        // 关闭动画
        function close() {
            $('#' + idText + '_box').animate({
                'top': data.top - 60,
                'opacity': '0',
            }, data.transition)
            // 删除DOM
            setTimeout(function () {
                $('#' + idText + '_box').remove();
            }, data.transition)
        }
        // 定时关闭
        var timers;
        if (!data.manual) {
            timers = setTimeout(close, data.timeout);
        }
        // 按钮关闭
        $('.' + idText + '_item.item3').click(function () {
            // 清除定时器
            clearInterval(timers)
            close();
        })
    } else if (data.type === 'alert') {
        // 是否禁止页面滚动
        if (data.pageScroll) {
            $('body').css('overflow', 'hidden')
        }
        // alert 提示框展示
        // 添加 alert 提示框
        $('#modail-dialog-box').append('<div id="' + idText + '_box"></div>')
        // 是否添加遮罩层
        if (data.mask) {
            $('#' + idText + '_box').append('<div class="' + idText + '_mask"></div>')
            // 设置mask外层弹框样式
            $('.' + idText + '_mask').css({
                position: 'fixed',
                top: 0,
                right: 0,
                left: 0,
                bottom: 0,
                background: 'rgba(0,0,0,0.4)',
            });
        }
        // 生成中间层
        $('#' + idText + '_box').append('<div class="' + idText + 'centerBox"></div>');

        $('.' + idText + 'centerBox').css({
            width: data.width + 'px',
            padding: '20px',
            background: '#ffffff',
            position: 'fixed',
            right: 0,
            left: 0,
            margin: 'auto',
            borderRadius: '5px',
            boxShadow: '0 1px 6px rgba(0,0,0,.2)',
            opacity: '0'
        })
        // 生成头部以及提示内容部分
        $('.' + idText + 'centerBox').append('<div class="' + idText + 'title"></div>')
        // 添加左侧图标
        $('.' + idText + 'title').append('<div class="' + idText + '_item item1"><img src="' + tipIconImg + '" /></div>');
        // 提示标题
        $('.' + idText + 'title').append('<div class="' + idText + '_item item2">' + data.title + '</div>');
        if (data.closable) {
            // 右侧关闭图标
            $('.' + idText + 'title').append('<div class="' + idText + '_item item3"><img src="' + close_modal + '" /></div>');
        }
        // 设置标题框文字样式
        $('.' + idText + '_item.item2').css({
            margin: '0 8px',
            fontSize: titleSize + 'px',
            color: '#333333'
        })
        // 设置文字关闭按钮靠边样式
        $('.' + idText + '_item.item3').css({
            position: 'absolute',
            right: '15px',
            cursor: 'pointer'
        })
        // 设置内层框公共样式
        $('.' + idText + '_item').css({
            display: 'inline-block'
        })
        $('.' + idText + '_item img').css({
            width: titleSize + 4 + 'px',
            verticalAlign: 'top',
            position: 'relative',
            top: '-1px'
        })
        // 设置提示框 box 样式
        $('.' + idText + 'title').css({
            marginBottom: '10px'
        })
        // 
        $('.' + idText + 'centerBox').append('<div class="' + idText + 'body">' + data.content + '</div>')
        // 设置样式
        $('.' + idText + 'body').css({
            padding: '0 ' + (titleSize + 10) + 'px'
        })
        // 计算距离顶部距离
        calculate($('.' + idText + 'centerBox'), 40);
        $('.' + idText + 'centerBox').css({
            top: data.top + 40,
        })
        // 显示动画
        $('.' + idText + 'centerBox').animate({
            'top': data.top,
            'opacity': '1',
        }, data.transition);
        // 关闭动画
        function close() {
            $('.' + idText + 'centerBox').animate({
                'top': data.top + 40,
                'opacity': '0',
            }, data.transition)
            // 删除DOM
            setTimeout(function () {
                $('#' + idText + '_box').remove();
                if (data.pageScroll) {
                    $('body').css('overflow', 'auto')
                }
            }, data.transition)
        }
        // 定时关闭
        var timers;
        if (!data.manual) {
            timers = setTimeout(close, data.timeout);
        }
        // 按钮关闭
        $('.' + idText + '_item.item3').click(function () {
            // 清除定时器
            clearInterval(timers)
            close();
        })
        // 遮罩层关闭
        if (data.maskClose) {
            $('.' + idText + '_mask').click(function () {
                // 清除定时器
                clearInterval(timers)
                close();
            })
        }
    } else if (data.type === 'confirm') {
        // 是否禁止页面滚动
        if (data.pageScroll) {
            $('body').css('overflow', 'hidden')
        }
        // alert 提示框展示
        // 添加 alert 提示框
        $('#modail-dialog-box').append('<div id="' + idText + '_box"></div>')
        // 是否添加遮罩层
        if (data.mask) {
            $('#' + idText + '_box').append('<div class="' + idText + '_mask"></div>')
            // 设置mask外层弹框样式
            $('.' + idText + '_mask').css({
                position: 'fixed',
                top: 0,
                right: 0,
                left: 0,
                bottom: 0,
                background: 'rgba(0,0,0,0.4)',
            });
        }
        // 生成中间层
        $('#' + idText + '_box').append('<div class="' + idText + 'centerBox"></div>');
        $('.' + idText + 'centerBox').css({
            width: data.width + 'px',
            padding: '20px',
            background: '#ffffff',
            position: 'fixed',
            right: 0,
            left: 0,
            margin: 'auto',
            borderRadius: '5px',
            boxShadow: '0 1px 6px rgba(0,0,0,.2)',
            opacity: '0'
        })

        // 生成头部以及提示内容部分
        $('.' + idText + 'centerBox').append('<div class="' + idText + 'title"></div>')
        // 添加左侧图标
        $('.' + idText + 'title').append('<div class="' + idText + '_item item1"><img src="' + tipIconImg + '" /></div>');
        // 提示标题
        $('.' + idText + 'title').append('<div class="' + idText + '_item item2">' + data.title + '</div>');
        if (data.closable) {
            // 右侧关闭图标
            $('.' + idText + 'title').append('<div class="' + idText + '_item item3"><img src="' + close_modal + '" /></div>');
        }
        // 设置标题框文字样式
        $('.' + idText + '_item.item2').css({
            margin: '0 8px',
            fontSize: titleSize + 'px',
            color: '#333333'
        })
        // 设置文字关闭按钮靠边样式
        $('.' + idText + '_item.item3').css({
            position: 'absolute',
            right: '15px',
            cursor: 'pointer'
        })
        // 设置内层框公共样式
        $('.' + idText + '_item').css({
            display: 'inline-block'
        })
        $('.' + idText + '_item img').css({
            width: titleSize + 4 + 'px',
            verticalAlign: 'top',
            position: 'relative',
            top: '-1px'
        })
        // 设置提示框 box 样式
        $('.' + idText + 'title').css({
            marginBottom: '10px'
        })
        // 
        $('.' + idText + 'centerBox').append('<div class="' + idText + 'body">' + data.content + '</div>')
        // 设置样式
        $('.' + idText + 'body').css({
            padding: '0 ' + (titleSize + 10) + 'px'
        })
        // 设置按钮
        $('.' + idText + 'centerBox').append('<div class="' + idText + 'footer"><button  class="button_s_model ' + idText + '_confirm">' + data.confirmText + '</button><button class="button_s_model ' + idText + '_cancel">' + data.cancelText + '</button><div class="clearBoth"></div></div>')
        $('.' + idText + 'footer').css({ paddingTop: '20px' })
        // 设置样式
        $('.button_s_model').css({
            float: 'right',
            marginLeft: '20px',
            outline: 'none',
            border: 'none',
            padding: '8px 30px',
            cursor: 'pointer',
            borderRadius: '5px',
        })
        // 计算距离顶部距离
        calculate($('.' + idText + 'centerBox'), 40)
        $('.' + idText + 'centerBox').css({
            top: data.top + 40,
        })

        // 显示动画
        $('.' + idText + 'centerBox').animate({
            'top': data.top,
            'opacity': '1',
        }, data.transition);

        // 鼠标移入移出
        $('.button_s_model').hover(function () {
            $(this).css({ 'opacity': '.8' })
        }, function () {
            $(this).css({ 'opacity': '1' })
        })
        // 鼠标按下
        $('.button_s_model').mousedown(function () {
            $(this).css({ 'opacity': '1' })
        }).mouseup(function () {
            $(this).css({ 'opacity': '.8' })
        })

        // 取消按钮样式
        $('.button_s_model.' + idText + '_cancel').css({
            background: '#fff',
            borderBox: 'box-sizing',
            border: '1px solid #eee',
        }).click(function () {
            data.cancel(close)
        })
        // 确认按钮样式
        $('.button_s_model.' + idText + '_confirm').css({
            background: '#2d8cf0',
            color: '#fff',
        }).click(function () {
            data.confirm(close)
        })

        // 清除浮动
        $('.clearBoth').css({
            clear: 'both'
        })

        // 关闭动画
        function close() {
            $('.' + idText + 'centerBox').animate({
                'top': data.top + 40,
                'opacity': '0',
            }, data.transition)
            // 删除DOM
            setTimeout(function () {
                $('#' + idText + '_box').remove();
                if (data.pageScroll) {
                    $('body').css('overflow', 'auto')
                }
            }, data.transition)
        }
        // 按钮关闭
        $('.' + idText + '_item.item3').click(function () {
            close();
        })
        // 遮罩层关闭
        if (data.maskClose) {
            $('.' + idText + '_mask').click(function () {
                close();
            })
        }
    }
}

// 错误提示
function $error(e){
    $modal({
        type: 'message', //弹框类型  'alert' or  'confirm' or 'message'  message提示(开启之前如果之前含有弹框则清除)
        icon: 'error', // 提示图标显示 'info' or 'success' or 'warning' or 'error'  or 'question'
        timeout: 2000, // 单位 ms  显示多少毫秒后关闭弹框 （ confirm 下无效 | 不传默认为 2000ms | 最短显示时间为500ms） 
        content: e, // 提示文字 
        center: true,// 是否绝对居中 默认为false  设置true后   top无效
        top:100, //距离顶部距离 单位px
        transition: 300, //过渡动画 默认 200   单位ms
        closable: true, // 是否显示可关闭按钮  默认为 false
    })
}

// 成功提示
function $success(e){
    $modal({
        type: 'message', //弹框类型  'alert' or  'confirm' or 'message'  message提示(开启之前如果之前含有弹框则清除)
        icon: 'success', // 提示图标显示 'info' or 'success' or 'warning' or 'error'  or 'question'
        timeout: 2000, // 单位 ms  显示多少毫秒后关闭弹框 （ confirm 下无效 | 不传默认为 2000ms | 最短显示时间为500ms） 
        content: e, // 提示文字 
        center: true,// 是否绝对居中 默认为false  设置true后   top无效
        top:100, //距离顶部距离 单位px
        transition: 300, //过渡动画 默认 200   单位ms
        closable: true, // 是否显示可关闭按钮  默认为 false
    })
}

